import React, { useState } from "react";


function StudentForm() {
  const [name, setName] = useState("");
  const [rollNumber, setRollNumber] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8000/api/students/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name, roll_number: rollNumber, phone_number: phoneNumber }),
      });

      if (response.ok) {
        console.log("Student added successfully");
        // Reset the form
        setName("");
        setRollNumber("");
        setPhoneNumber("");
      } else {
        console.error("Failed to add student");
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <br />
      <br />
      <form  style={{background: 'grey'}} onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input style={{float:'right', paddingRight:'20px', marginLeft:'10px'}} type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <br />
        <div>
          <label>Roll Number:</label>
          <input style={{float:'right', paddingRight:'20px', marginLeft:'10px'}} type="text" value={rollNumber} onChange={(e) => setRollNumber(e.target.value)} />
        </div>
        <br />
        <div>
          <label>Phone Number:</label>
          <input style={{float:'right', paddingRight:'20px', marginLeft:'10px'}} type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
        </div>
        <br />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}



function StudentDetails() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showError, setShowError] = useState(false);
  const [showAddStudent, setShowAddStudent] = useState(false);

  const handleStudentSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm) {
      setShowError(true);
      return;
    }
    try {
      const response = await fetch(`http://localhost:8000/api/search/?q=${searchTerm}`);
      const data = await response.json();
      setSearchResults(data);
      setShowError(false);
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddStudent = () => {
    setShowAddStudent(true);
  };

  const handleCloseAddStudent = () => {
    setShowAddStudent(false);
  };

  return (
    <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', fontSize:'14px'}}>
      <h1 style={{color:'white'}}>Student Details</h1>
      <form onSubmit={handleStudentSearch}>
        <input
          type="text"
          placeholder="Search by Roll Number"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
        {showError && (
          <p style={{ color: "red" }}>Please enter a search term</p>
        )}
      </form>
      <button onClick={handleAddStudent} style={{margin: '20px'}}>
        <i className="fa fa-plus"></i> Add Student
      </button>
      {showAddStudent && (
        <div style={{position: 'fixed', top: '0', left: '0', right: '0', bottom: '0', backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
          <div style={{backgroundColor: '#fff', padding: '20px'}}>
              <StudentForm />
            <button onClick={handleCloseAddStudent}>Close</button>
          </div>
        </div>
      )}
      {searchResults.map((result) => (
        <div key={result.id} style={{color:'white'}}>
          <h2>{result.name}</h2>
          <p>Roll Number: {result.roll_num}</p>
          <p>Phone: {result.phone_number}</p>
        </div>
      ))}
    </div>
  );
}

export default StudentDetails;
