import axios from "axios";
import { useNavigate } from "react-router-dom";
import {useState} from "react";
import '../App.css';

 const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const submit = async e => {
        e.preventDefault();

        const user = {
            username: username,
            password: password
          };

        const {data} = await axios.post('http://localhost:8000/token/', user ,{headers: {
            'Content-Type': 'application/json'
        }}, {withCredentials: true});

        console.log(data)
        localStorage.clear();
        localStorage.setItem('access_token', data.access);
        localStorage.setItem('refresh_token', data.refresh);
        axios.defaults.headers.common['Authorization'] = `Bearer ${data['access']}`;
        window.location.href = '/home'


    }

    return(
        <div className="Auth-form-container" >
        <form className="Auth-form" onSubmit={submit}>
          <div className="Auth-form-content">
            <h3 className="Auth-form-title">Sign In</h3>
            <div className="form-group mt-3">
              <label style={{padding:'10px'}}>Username</label>
              <input
                style={{borderRadius:'10px', padding:'10px'}}
                className="form-control mt-1"
                placeholder="Enter Username"
                name='username'
                type='text'
                value={username}
                required
                onChange={e => setUsername(e.target.value)}
              />
            </div>
            <br />
            <div className="form-group mt-3">
              <label style={{padding:'10px'}}>Password</label>
              <input 
                style={{borderRadius:'10px', padding:'10px'}}
                name='password'
                type="password"
                className="form-control mt-1"
                placeholder="Enter password"
                value={password}
                required
                onChange={e => setPassword(e.target.value)}
              />
            </div>
            <br />
            <div className="d-grid gap-2 mt-3">
              <button style={{borderRadius:'10px', fontSize:'15px', padding:'5px', }} type="submit" className="btn btn-primary">
                Submit
              </button>
            </div>
          </div>
        </form>
    </div>

    )
}

export default Login;
