import React from "react";
import Chart from "react-google-charts";

class DynamicChart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      cgpaValues: this.props.cgpaValues,
    };
  }

  render() {
    const data = [["CGPA", "Score"]];

    this.state.cgpaValues.forEach((cgpa, index) => {
      data.push([`Semester${index+1}`, cgpa]);
    });

    return (
      <div style={{ display: "flex", justifyContent: "center" }}>
        <Chart
          width={"500px"}
          height={"300px"}
          chartType="BarChart"
          loader={<div>Loading Chart</div>}
          data={data}
          options={{
            title: "CGPA Chart",
          }}
          rootProps={{ "data-testid": "1" }}
        />
      </div>
    );
  }
}

export default DynamicChart;
