function StudentDetails() {
  const [rollNum, setRollNum] = useState("");
  const [semesterNum, setSemesterNum] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showError, setShowError] = useState(false);
  const [matchError, setMatchError] = useState(false);
  const [chartData, setChartData] = useState(null);
  // const [showAddStudent, setShowAddStudent] = useState(false);

  const getChartData = async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/chart/?rollNum=${rollNum}&semesterNum=${semesterNum}`);
      const data = await response.json();
      setChartData(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleStudentSearch = async (e) => {
    e.preventDefault();
    if (!rollNum) {
      setShowError(true);
      return;
    }
    try {
      const response = await fetch(`http://localhost:8000/api/search/?rollNum=${rollNum}&semesterNum=${semesterNum}`);
      const data = await response.json();
      setSearchResults(data);
      setShowError(false);
      console.log(data);
      data.length === 0 ? setMatchError(true) : setMatchError(false);

      getChartData();
      {chartData && (
        <canvas id="myChart" width="400" height="400" style={{ marginBottom: "30px" }}></canvas>
      )}

      React.useEffect(() => {
  if (chartData) {
    const labels = chartData.map((item) => item.semester_num);
    const cgpaData = chartData.map((item) => item.cgpa);

    const ctx = document.getElementById("myChart").getContext("2d");

    new Chart(ctx, {
      type: "line",
      data: {
        labels: labels,
        datasets: [
          {
            label: "CGPA",
            data: cgpaData,
            borderColor: "rgba(255, 99, 132, 1)",
            borderWidth: 1,
            fill: false,
          },
        ],
      },
      options: {
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
      },
    });
  }
}, [chartData]);

    } catch (error) {
      console.error(error);
    }

  };
  return (
    <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', fontSize:'14px'}}>
      <h1 style={{color:'white'}}>Student Details</h1>
      <br />
      <br />
      <form style={{flex:'column'}} onSubmit={handleStudentSearch}>
        <input
          type="text"
          placeholder="Roll Number"
          value={rollNum}
          onChange={(e) => setRollNum(e.target.value)}
          style={{borderRadius:'10px', height:'40px', background:'lightGrey', paddingLeft:'5px'}}
        />
        <br />
        <input
          type="text"
          placeholder="Semester Number"
          value={semesterNum}
          onChange={(e) => setSemesterNum(e.target.value)}
          style={{borderRadius:'10px', height:'40px', background:'lightGrey', paddingLeft:'5px'}}
        />
        <br />
        <br />
        <button style={{marginLeft:'55px', background:'lightGrey', borderRadius:'10px', fontSize:'11px', padding:'3px'}} type="submit">Search</button>
        {showError && (
          <p style={{ color: "red" }}>Please enter a search term</p>
        )}
        {matchError && (
          <p style={{ color: "red", marginLeft:'30px'}}>No results found!</p>
        )}


      </form>
      {searchResults.map((result) => (
        <div key={result.id} style={{color:'white'}}>
          <br />
          <br />
          <h2>{result.student__name}</h2>
          <p>Roll Number: <span style={{marginLeft:'5px'}}>{result.student__roll_num}</span></p>
          <p>Phone: <span style={{marginLeft:'5px'}}>{result.student__phone_number}</span></p>
          <p>CGPA: <span style={{marginLeft:'5px'}}>{result.cgpa}</span></p>
        </div>
      ))}
    </div>
  );
}

export default StudentDetails;
